package com.ssm.service.impl;import com.ssm.mapper.SyRoleMapper;import com.ssm.model.SyRole;import com.ssm.service.SyRoleService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class SyRoleServiceImpl implements SyRoleService {    @Autowired    private SyRoleMapper syRoleMapper;    @Override    public int deleteByPrimaryKey(Short id) {        return syRoleMapper.deleteByPrimaryKey(id);    }    @Override    public int insert(SyRole record) {        return syRoleMapper.insert(record);    }    @Override    public List<SyRole> selectByPrimaryKeyLike(int start, int end, String rolename, String disabled) {        return syRoleMapper.selectByPrimaryKeyLike(start,end,rolename,disabled);    }    @Override    public int selectCount(String rolename, String disabled) {        return syRoleMapper.selectCount(rolename,disabled);    }    @Override    public int updateByPrimaryKey(SyRole record) {        return syRoleMapper.updateByPrimaryKey(record);    }    @Override    public List<SyRole> selectRole() {        return syRoleMapper.selectRole();    }    @Override    public SyRole byid(short id) {        return syRoleMapper.byid(id);    }}